aiperjmv <- function(
    r = 0.3,
    w = 0.1,
    conf.level = 95) {

    if ( ! requireNamespace('jmvcore'))
        stop('aiperjmv requires jmvcore to be installed (restart may be required)')


    options <- aiperjmvOptions$new(
        r = r,
        w = w,
        conf.level = conf.level)

    analysis <- aiperjmvClass$new(
        options = options,
        data = NULL) #data)

    analysis$run()

    analysis$results
}
