#' @export
multiResponsejmv <- if (requireNamespace('jmvcore', quietly=TRUE)) function(
    data,
    items,
    endorsedOption = 1) {

    if ( ! requireNamespace('jmvcore'))
        stop('multiResponsejmv requires jmvcore to be installed (restart may be required)')

    if ( ! missing(items)) items <- jmvcore::resolveQuo(jmvcore::enquo(items))
    if (missing(data))
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(items), items, NULL))


    options <- multiResponsejmvOptions$new(
        items = items,
        endorsedOption = endorsedOption)

    analysis <- multiResponsejmvClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}
